// src/components/product.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({ name: '', price: '', quantity: '' });
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (error) {
      alert('Error fetching products');
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleAdd = async () => {
    const { name, price, quantity } = formData;
    if (!name || !price || !quantity) {
      alert('Please fill all fields');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/products', {
        name,
        price: parseFloat(price),
        quantity: parseInt(quantity, 10)
      });
      alert('Product added successfully');
      setFormData({ name: '', price: '', quantity: '' });
      fetchProducts();
    } catch (error) {
      alert('Failed to add product');
      console.error(error);
    }
  };

  const handleUpdate = async () => {
    if (!selectedId) {
      alert('Please select a product to update');
      return;
    }
    const { name, price, quantity } = formData;
    if (!name || !price || !quantity) {
      alert('Please fill all fields');
      return;
    }
    try {
      await axios.put(`http://localhost:5000/api/products/${selectedId}`, {
        name,
        price: parseFloat(price),
        quantity: parseInt(quantity, 10)
      });
      alert('Product updated successfully');
      setFormData({ name: '', price: '', quantity: '' });
      setSelectedId(null);
      fetchProducts();
    } catch (error) {
      alert('Failed to update product');
      console.error(error);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      alert('Product deleted successfully');
      if (id === selectedId) {
        setFormData({ name: '', price: '', quantity: '' });
        setSelectedId(null);
      }
      fetchProducts();
    } catch (error) {
      alert('Failed to delete product');
      console.error(error);
    }
  };

  const handleSelect = (product) => {
    setFormData({
      name: product.name,
      price: product.price,
      quantity: product.quantity
    });
    setSelectedId(product._id);
  };

  const handleClear = () => {
    setFormData({ name: '', price: '', quantity: '' });
    setSelectedId(null);
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h2>Product Management</h2>

      <input
        name="name"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
        style={{ marginRight: 10, padding: 5 }}
      />
      <input
        name="price"
        value={formData.price}
        onChange={handleChange}
        placeholder="Price"
        type="number"
        style={{ marginRight: 10, padding: 5 }}
      />
      <input
        name="quantity"
        value={formData.quantity}
        onChange={handleChange}
        placeholder="Quantity"
        type="number"
        style={{ marginRight: 10, padding: 5 }}
      />

      <div style={{ marginTop: 10 }}>
        <button onClick={handleAdd} style={{ marginRight: 10 }}>Add</button>
        <button onClick={handleUpdate} disabled={!selectedId} style={{ marginRight: 10 }}>Update</button>
        <button onClick={handleClear}>Clear</button>
      </div>

      <table style={{ width: '100%', marginTop: 20, borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ borderBottom: '1px solid black' }}>
            <th style={{ textAlign: 'left', padding: '8px' }}>Name</th>
            <th style={{ textAlign: 'right', padding: '8px' }}>Price</th>
            <th style={{ textAlign: 'right', padding: '8px' }}>Quantity</th>
            <th style={{ padding: '8px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr><td colSpan="4" style={{ textAlign: 'center', padding: '10px' }}>No products found</td></tr>
          ) : (
            products.map(product => (
              <tr
                key={product._id}
                style={{ backgroundColor: product._id === selectedId ? '#d3f9d8' : 'transparent' }}
              >
                <td style={{ padding: '8px' }}>{product.name}</td>
                <td style={{ padding: '8px', textAlign: 'right' }}>{product.price.toFixed(2)}</td>
                <td style={{ padding: '8px', textAlign: 'right' }}>{product.quantity}</td>
                <td style={{ padding: '8px', textAlign: 'center' }}>
                  <button onClick={() => handleSelect(product)} style={{ marginRight: 5 }}>Select</button>
                  <button onClick={() => handleDelete(product._id)} style={{ backgroundColor: '#ff6b6b', color: 'white' }}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Product;
