import React, { useState } from 'react';
import axios from 'axios';

function Billing({ selectedProduct }) {
    const [customerName, setCustomerName] = useState('');
    const [phone, setPhone] = useState('');
    const [spokenText, setSpokenText] = useState('');

    const handleMicClick = () => {
        const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
        recognition.lang = 'en-US';
        recognition.interimResults = false;
        recognition.maxAlternatives = 1;

        recognition.onresult = (event) => {
            const transcript = event.results[0][0].transcript;
            setSpokenText(transcript);
            alert("Voice Input: " + transcript);
        };

        recognition.onerror = (event) => {
            console.error("Speech recognition error", event.error);
        };

        recognition.start();
    };

    const handleGenerateBill = () => {
        if (!selectedProduct || !customerName || !phone) {
            alert("Please fill all fields and select a product");
            return;
        }

        const bill = {
            customerName,
            phone,
            products: [
                {
                    name: selectedProduct.name,
                    quantity: 1,
                    price: selectedProduct.price
                }
            ]
        };

        axios.post('http://localhost:5000/api/bill', bill)
            .then(res => alert("Bill generated and saved!"))
            .catch(err => alert("Error generating bill."));
    };

    return (
        <div style={{ width: '70%', padding: '10px' }}>
            <h2>Billing</h2>
            <div>
                <label>Customer Name:</label>
                <input value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
            </div>
            <div>
                <label>Phone:</label>
                <input value={phone} onChange={(e) => setPhone(e.target.value)} />
            </div>
            <div style={{ marginTop: '20px' }}>
                <button onClick={handleMicClick}>ðŸŽ¤ Click Mic to Speak</button>
                <p>Spoken Text: {spokenText}</p>
            </div>
            {selectedProduct && (
                <div style={{ marginTop: '20px' }}>
                    <h3>Selected Product</h3>
                    <p>Name: {selectedProduct.name}</p>
                    <p>Price: â‚¹{selectedProduct.price}</p>
                </div>
            )}
            <button onClick={handleGenerateBill} style={{ marginTop: '20px' }}>Generate Bill</button>
        </div>
    );
}

export default Billing;
