// product.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({ name: '', quantity: '', price: '' });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/api/products');
    setProducts(res.data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    if (formData.name && formData.quantity && formData.price) {
      await axios.post('http://localhost:5000/api/products', formData);
      fetchProducts();
      handleClear();
    }
  };

  const handleUpdate = async () => {
    if (editId) {
      await axios.put(`http://localhost:5000/api/products/${editId}`, formData);
      fetchProducts();
      handleClear();
    }
  };

  const handleDelete = async () => {
    if (editId) {
      await axios.delete(`http://localhost:5000/api/products/${editId}`);
      fetchProducts();
      handleClear();
    }
  };

  const handleClear = () => {
    setFormData({ name: '', quantity: '', price: '' });
    setEditId(null);
  };

  const handleEdit = (product) => {
    setFormData({ name: product.name, quantity: product.quantity, price: product.price });
    setEditId(product._id);
  };

  return (
    <div>
      <h2>Product Management</h2>
      <input name="name" placeholder="Product Name" value={formData.name} onChange={handleChange} />
      <input name="quantity" placeholder="Quantity" value={formData.quantity} onChange={handleChange} />
      <input name="price" placeholder="Price" value={formData.price} onChange={handleChange} />
      <button onClick={handleAdd}>Add</button>
      <button onClick={handleUpdate}>Update</button>
      <button onClick={handleDelete}>Delete</button>
      <button onClick={handleClear}>Clear</button>
      <ul>
        {products.map(product => (
          <li key={product._id} onClick={() => handleEdit(product)}>
            {product.name} - {product.quantity} - Rs.{product.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Product;
