import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [listening, setListening] = useState(false);
  const [status, setStatus] = useState(''); // status message
  const recognitionRef = useRef(null);

  // Fetch products on mount
  const fetchProducts = () => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(() => setProducts([]));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Speech synthesis helper
  const speak = (text) => {
    const utter = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utter);
  };

  // Initialize SpeechRecognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Speech Recognition API not supported');
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = 'en-US';

    recognitionRef.current.onend = () => {
      setListening(false);
    };
  }, []);

  // Start voice command flow
  const startListening = () => {
    if (!recognitionRef.current) return;
    setStatus('Say: Add, Update or Delete');
    speak('Welcome to Product Section. Please say add, update, or delete.');
    recognitionRef.current.onresult = handleMainCommand;
    recognitionRef.current.start();
    setListening(true);
  };

  // Handle main voice commands: add, update, delete
  const handleMainCommand = (event) => {
    const command = event.results[0][0].transcript.toLowerCase();
    setStatus(`Heard command: ${command}`);
    if (command.includes('add')) {
      speak('Adding new product. Please say product name.');
      recognitionRef.current.onresult = handleAddName;
      recognitionRef.current.start();
    } else if (command.includes('update')) {
      speak('Updating product. Please say the product name.');
      recognitionRef.current.onresult = handleUpdateName;
      recognitionRef.current.start();
    } else if (command.includes('delete')) {
      speak('Deleting product. Please say the product name.');
      recognitionRef.current.onresult = handleDeleteName;
      recognitionRef.current.start();
    } else {
      speak('Sorry, command not recognized. Please say add, update or delete.');
      recognitionRef.current.start();
    }
  };

  // Add product flow handlers
  const handleAddName = (event) => {
    const name = event.results[0][0].transcript.trim();
    setStatus(`Adding product: ${name}`);
    speak(`Got product name ${name}. Please say price in numbers.`);
    recognitionRef.current.onresult = (e) => handleAddPrice(e, name);
    recognitionRef.current.start();
  };

  const handleAddPrice = (event, name) => {
    const priceText = event.results[0][0].transcript.trim();
    const price = parseFloat(priceText.replace(/[^0-9.]/g, ''));
    if (isNaN(price)) {
      speak('Price not understood. Please say price in numbers again.');
      recognitionRef.current.start();
      return;
    }
    speak(`Price is ${price}. Please say quantity.`);
    recognitionRef.current.onresult = (e) => handleAddQuantity(e, name, price);
    recognitionRef.current.start();
  };

  const handleAddQuantity = (event, name, price) => {
    const qtyText = event.results[0][0].transcript.trim();
    const quantity = parseInt(qtyText.replace(/[^0-9]/g, ''), 10);
    if (isNaN(quantity)) {
      speak('Quantity not understood. Please say quantity again.');
      recognitionRef.current.start();
      return;
    }

    // Save product via API
    axios.post('http://localhost:5000/api/products', { name, price, quantity })
      .then(() => {
        speak(`Product ${name} added successfully.`);
        fetchProducts();
        setStatus(`Product ${name} added.`);
      })
      .catch(() => {
        speak('Failed to add product.');
        setStatus('Failed to add product.');
      });
  };

  // Update product flow handlers
  const handleUpdateName = (event) => {
    const name = event.results[0][0].transcript.trim();
    const product = products.find(p => p.name.toLowerCase() === name.toLowerCase());
    if (!product) {
      speak(`Product ${name} not found. Please say product name again.`);
      recognitionRef.current.start();
      return;
    }
    speak(`What do you want to update? Name, price or quantity?`);
    recognitionRef.current.onresult = (e) => handleUpdateField(e, product);
    recognitionRef.current.start();
  };

  const handleUpdateField = (event, product) => {
    const field = event.results[0][0].transcript.trim().toLowerCase();
    if (!['name', 'price', 'quantity'].includes(field)) {
      speak('Invalid field. Please say name, price or quantity.');
      recognitionRef.current.start();
      return;
    }
    speak(`Please say the new value for ${field}.`);
    recognitionRef.current.onresult = (e) => handleUpdateValue(e, product, field);
    recognitionRef.current.start();
  };

  const handleUpdateValue = (event, product, field) => {
    let newValue = event.results[0][0].transcript.trim();

    if (field === 'price') {
      const price = parseFloat(newValue.replace(/[^0-9.]/g, ''));
      if (isNaN(price)) {
        speak('Price not understood. Please say price again.');
        recognitionRef.current.start();
        return;
      }
      newValue = price;
    }
    else if (field === 'quantity') {
      const quantity = parseInt(newValue.replace(/[^0-9]/g, ''), 10);
      if (isNaN(quantity)) {
        speak('Quantity not understood. Please say quantity again.');
        recognitionRef.current.start();
        return;
      }
      newValue = quantity;
    }

    // Update product API call
    axios.put(`http://localhost:5000/api/products/${product._id}`, {
      ...product,
      [field]: newValue
    })
      .then(() => {
        speak(`Product ${field} updated successfully.`);
        fetchProducts();
        setStatus(`Updated ${product.name} ${field} to ${newValue}.`);
      })
      .catch(() => {
        speak('Failed to update product.');
        setStatus('Failed to update product.');
      });
  };

  // Delete product flow handler
  const handleDeleteName = (event) => {
    const name = event.results[0][0].transcript.trim();
    const product = products.find(p => p.name.toLowerCase() === name.toLowerCase());
    if (!product) {
      speak(`Product ${name} not found. Please say product name again.`);
      recognitionRef.current.start();
      return;
    }

    axios.delete(`http://localhost:5000/api/products/${product._id}`)
      .then(() => {
        speak(`Product ${product.name} deleted successfully.`);
        fetchProducts();
        setStatus(`Deleted product ${product.name}.`);
      })
      .catch(() => {
        speak('Failed to delete product.');
        setStatus('Failed to delete product.');
      });
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h2>Product Management</h2>
      <button
        onClick={startListening}
        disabled={listening}
        style={{ fontSize: '20px', padding: '10px 20px', cursor: 'pointer' }}
      >
        ðŸŽ¤ {listening ? 'Listening...' : 'Start Voice Control'}
      </button>
      <p><i>{status}</i></p>

      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', marginTop: 20 }}>
        <thead>
          <tr style={{ backgroundColor: '#007bff', color: 'white' }}>
            <th>Name</th>
            <th>Price (Rs.)</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr><td colSpan="3">No products found.</td></tr>
          ) : (
            products.map(p => (
              <tr key={p._id}>
                <td>{p.name}</td>
                <td>{p.price}</td>
                <td>{p.quantity}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Product;
