import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ZenBot from './ZenBot';  // make sure this is default export in ZenBot.jsx
import './LandingPage.css';     // your styles here

function LandingPage() {
  const [showChatbot, setShowChatbot] = useState(false);

  const toggleChatbot = () => {
    setShowChatbot(prev => !prev);
  };

  return (
    <div className="landing-container">
      <h1>Zenventory</h1>

      <div className="button-group">
        <Link to="/products"><button>Products</button></Link>
        <Link to="/billing"><button>Billing</button></Link>
      </div>

      {/* Chatbot Icon Button */}
      <button 
        className="chatbot-icon-btn" 
        onClick={toggleChatbot}
        aria-label="Toggle Chatbot"
        title="Chat with ZenBot"
      >
        ðŸ’¬ {/* You can replace this with any icon or SVG */}
      </button>

      {/* Chatbot Component */}
      {showChatbot && (
        <div className="chatbot-wrapper">
          <ZenBot />
        </div>
      )}
    </div>
  );
}

export default LandingPage;
