import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Billing() {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState({ name: '', contact: '' });
  const [billText, setBillText] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => alert('Failed to fetch products'));
  }, []);

  const handleAddToCart = (item) => {
    const qty = parseInt(prompt(`Enter quantity for ${item.name}`), 10);
    if (isNaN(qty) || qty <= 0) return;

    let total = qty * item.price;
    let discount = 0;

    // Discount based on quantity
    if (qty >= 5) discount = 0.10;
    else if (qty >= 3) discount = 0.05;

    const discountedTotal = total - (total * discount);

    alert(`Discount Applied: ${discount * 100}%`);

    setCart([...cart, { ...item, qty, total: discountedTotal, discount }]);
  };

  const handleGenerateBill = async () => {
    if (!customer.name || !customer.contact) {
      alert('Please enter customer name and contact');
      return;
    }
    if (cart.length === 0) {
      alert('Cart is empty!');
      return;
    }

    let total = 0, discountAmt = 0;
    let lines = `\t\tZenventory\nPhone No. 937304****, Fairborn-34567\n${'='.repeat(46)}\nCustomer Name: ${customer.name}\tPhone No.: ${customer.contact}\nBill No.: ${Date.now()}\t\tDate: ${(new Date()).toLocaleDateString()}\n${'='.repeat(46)}\nProduct Name\t\tQTY\tPrice\n`;

    for (const item of cart) {
      lines += `${item.name}\t\t${item.qty}\t${item.total.toFixed(2)}\n`;
      total += item.total;
      discountAmt += item.total * item.discount;
    }

    // Check loyalty status
    let loyaltyDiscount = 0;
    try {
      const loyaltyRes = await axios.post('http://localhost:5000/api/loyalty/check', customer);
      if (loyaltyRes.data.exists) {
        loyaltyDiscount = 0.05; // 5% extra discount
        alert(`Returning customer! Loyalty coins: ${loyaltyRes.data.coins}. Additional 5% discount applied!`);
      } else {
        const registerRes = await axios.post('http://localhost:5000/api/loyalty/register', customer);
        alert(`Welcome new customer! Loyalty coins earned: ${registerRes.data.coins}`);
      }
    } catch (error) {
      alert('Error checking loyalty status.');
    }

    const loyaltyAmount = total * loyaltyDiscount;
    const netPay = total - discountAmt - loyaltyAmount;

    lines += `${'='.repeat(46)}\nBill Amount\t\t\tRs.${total.toFixed(2)}\n`;
    lines += `Discount\tRs.${discountAmt.toFixed(2)}\n`;
    lines += `Loyalty Discount\tRs.${loyaltyAmount.toFixed(2)}\n`;
    lines += `Net Pay\t\t\tRs.${netPay.toFixed(2)}\n`;
    lines += `${'='.repeat(46)}\nThank You for your Business!`;

    setBillText(lines);

    // Save bill as .txt file
    const element = document.createElement('a');
    const file = new Blob([lines], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `bill_${Date.now()}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div style={{ display: 'flex', gap: '20px', padding: '10px' }}>
      {/* Left: Product Filter */}
      <div style={{ flex: 1 }}>
        <input
          placeholder="Search products"
          value={search}
          onChange={e => {
            setSearch(e.target.value);
            setFiltered(products.filter(p => p.name.toLowerCase().includes(e.target.value.toLowerCase())));
          }}
          style={{ width: '90%', padding: '5px', marginBottom: '10px' }}
        />
        <ul style={{ maxHeight: '400px', overflowY: 'auto', listStyle: 'none', padding: 0 }}>
          {(search ? filtered : products).map(p => (
            <li key={p._id} style={{ marginBottom: '8px' }}>
              {p.name} ({p.quantity}){' '}
              <button onClick={() => handleAddToCart(p)}>Add</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Middle: Customer Details & Cart */}
      <div style={{ flex: 1 }}>
        <h3>Customer Details</h3>
        <input
          placeholder="Name"
          value={customer.name}
          onChange={e => setCustomer({ ...customer, name: e.target.value })}
          style={{ width: '90%', padding: '5px', marginBottom: '10px' }}
        />
        <input
          placeholder="Contact"
          value={customer.contact}
          onChange={e => setCustomer({ ...customer, contact: e.target.value })}
          style={{ width: '90%', padding: '5px', marginBottom: '10px' }}
        />
        <h4>Cart Items</h4>
        <ul style={{ maxHeight: '350px', overflowY: 'auto', listStyle: 'none', padding: 0 }}>
          {cart.map((item, index) => (
            <li key={index}>
              {item.name} - {item.qty} pcs - Rs.{item.total.toFixed(2)}
            </li>
          ))}
        </ul>
      </div>

      {/* Right: Bill Section */}
      <div style={{ flex: 1 }}>
        <h3>Bill</h3>
        <button onClick={handleGenerateBill} style={{ padding: '10px', marginBottom: '10px' }}>
          Generate Bill
        </button>
        <pre style={{ whiteSpace: 'pre-wrap', backgroundColor: '#f0f0f0', padding: '10px', minHeight: '400px' }}>
          {billText}
        </pre>
      </div>
    </div>
  );
}

export default Billing;
