import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Product.css';

function Product() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', quantity: '', price: '' });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/products');
    setProducts(res.data);
  };

  const handleVoiceCommand = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    const synth = window.speechSynthesis;

    const speak = (msg) => synth.speak(new SpeechSynthesisUtterance(msg));

    speak("Welcome to product section. Would you like to add, update, or delete?");
    recognition.start();

    recognition.onresult = async (event) => {
      const command = event.results[0][0].transcript.toLowerCase();

      if (command.includes("add")) {
        speak("Please say product name.");
        recognition.start();
        recognition.onresult = (e1) => {
          const name = e1.results[0][0].transcript;
          speak("Now say quantity");
          recognition.start();
          recognition.onresult = (e2) => {
            const quantity = e2.results[0][0].transcript;
            speak("Now say price");
            recognition.start();
            recognition.onresult = async (e3) => {
              const price = e3.results[0][0].transcript;
              await axios.post('http://localhost:5000/products', { name, quantity, price });
              speak("Product added successfully");
              fetchProducts();
            };
          };
        };

      } else if (command.includes("delete")) {
        speak("Please say product name to delete.");
        recognition.start();
        recognition.onresult = async (e) => {
          const name = e.results[0][0].transcript;
          await axios.delete(`http://localhost:5000/products/${name}`);
          speak("Product deleted successfully");
          fetchProducts();
        };

      } else if (command.includes("update")) {
        speak("Please say product name to update.");
        recognition.start();
        recognition.onresult = (e1) => {
          const name = e1.results[0][0].transcript;
          speak("Say new quantity.");
          recognition.start();
          recognition.onresult = (e2) => {
            const quantity = e2.results[0][0].transcript;
            speak("Say new price.");
            recognition.start();
            recognition.onresult = async (e3) => {
              const price = e3.results[0][0].transcript;
              await axios.put(`http://localhost:5000/products/${name}`, { quantity, price });
              speak("Product updated successfully");
              fetchProducts();
            };
          };
        };

      } else {
        speak("Command not recognized");
      }
    };
  };

  return (
    <div className="product-container">
      <h2>Products</h2>
      <button onClick={handleVoiceCommand}>ðŸŽ¤ Voice Control</button>
      <ul>
        {products.map((p, idx) => (
          <li key={idx}>{p.name} - {p.quantity} - ${p.price}</li>
        ))}
      </ul>
    </div>
  );
}

export default Product;
