// src/components/product.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({ name: '', price: '', quantity: '' });
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/api/products');
    setProducts(res.data);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    if (!formData.name || !formData.price || !formData.quantity) return;
    await axios.post('http://localhost:5000/api/products', formData);
    setFormData({ name: '', price: '', quantity: '' });
    fetchProducts();
  };

  const handleUpdate = async () => {
    if (!selectedId) return;
    await axios.put(`http://localhost:5000/api/products/${selectedId}`, formData);
    setFormData({ name: '', price: '', quantity: '' });
    setSelectedId(null);
    fetchProducts();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/products/${id}`);
    fetchProducts();
  };

  const handleSelect = (product) => {
    setFormData({ name: product.name, price: product.price, quantity: product.quantity });
    setSelectedId(product._id);
  };

  const handleClear = () => {
    setFormData({ name: '', price: '', quantity: '' });
    setSelectedId(null);
  };

  return (
    <div>
      <h2>Product Management</h2>
      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" />
      <input name="price" value={formData.price} onChange={handleChange} placeholder="Price" type="number" />
      <input name="quantity" value={formData.quantity} onChange={handleChange} placeholder="Quantity" type="number" />
      <button onClick={handleAdd}>Add</button>
      <button onClick={handleUpdate} disabled={!selectedId}>Update</button>
      <button onClick={handleClear}>Clear</button>

      <table>
        <thead>
          <tr><th>Name</th><th>Price</th><th>Quantity</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product._id}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.quantity}</td>
              <td>
                <button onClick={() => handleSelect(product)}>Select</button>
                <button onClick={() => handleDelete(product._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Product;
