import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import ZenBot from './ZenBot';
import './LandingPage.css';

function LandingPage() {
  const [showChatbot, setShowChatbot] = useState(false);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);
  const navigate = useNavigate();

  // Setup Web Speech API
  useEffect(() => {
    if (!('webkitSpeechRecognition' in window)) {
      alert('Speech Recognition API not supported in this browser.');
      return;
    }
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.continuous = false;
    recognition.interimResults = false;
    recognitionRef.current = recognition;

    recognition.onresult = (event) => {
      const speechResult = event.results[0][0].transcript.toLowerCase();
      setListening(false);
      // Process commands for landing page
      if (speechResult.includes('product')) {
        speak('Opening product page');
        navigate('/product');
      } else if (speechResult.includes('bill')) {
        speak('Opening billing page');
        navigate('/billing');
      } else {
        speak('Sorry, please say product or bill');
      }
    };

    recognition.onerror = (event) => {
      setListening(false);
      console.error('Speech recognition error', event.error);
      speak('Sorry, I did not get that. Please try again.');
    };

  }, [navigate]);

  // Speech synthesis helper
  const speak = (text) => {
    const utterance = new SpeechSynthesisUtterance(text);
    speechSynthesis.speak(utterance);
  };

  // Start voice recognition
  const startListening = () => {
    if (recognitionRef.current && !listening) {
      setListening(true);
      speak('Welcome to Zenventory. What section would you like to open? Product or Bill?');
      recognitionRef.current.start();
    }
  };

  return (
    <div className="landing-container">
      <h1>Zenventory</h1>
      <div className="button-group">
        <Link to="/product"><button>Products</button></Link>
        <Link to="/billing"><button>Billing</button></Link>
      </div>

      {/* Chatbot Icon Button */}
      <button 
        className="chatbot-icon-btn" 
        onClick={() => setShowChatbot(prev => !prev)}
        aria-label="Toggle Chatbot"
        title="Chat with ZenBot"
      >
        ðŸ’¬
      </button>

      {/* Mic Icon Button */}
      <button
        className={`mic-icon-btn ${listening ? 'listening' : ''}`}
        onClick={startListening}
        aria-label="Activate Voice Control"
        title="Voice Control"
      >
        ðŸŽ¤
      </button>

      {showChatbot && (
        <div className="chatbot-wrapper">
          <ZenBot />
        </div>
      )}
    </div>
  );
}

export default LandingPage;
