// src/components/billing.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Billing() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState({ name: '', contact: '' });
  const [loyalty, setLoyalty] = useState([]);

  useEffect(() => {
    fetchProducts();
    fetchLoyalty();
  }, []);

  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/api/products');
    setProducts(res.data);
  };

  const fetchLoyalty = async () => {
    const res = await axios.get('http://localhost:5000/api/loyalty');
    setLoyalty(res.data);
  };

  const handleAddToCart = (product) => {
    const quantity = prompt(`Enter quantity for ${product.name}:`);
    if (!quantity || isNaN(quantity) || quantity <= 0) return;

    const totalPrice = product.price * quantity;
    let discount = 0;

    if (quantity >= 5 || totalPrice >= 500) {
      discount = 0.1 * totalPrice;
      alert(`10% discount applied on ${product.name}`);
    }

    const item = {
      name: product.name,
      price: product.price,
      quantity: Number(quantity),
      totalPrice,
      discount,
    };

    setCart([...cart, item]);
  };

  const handleCustomerChange = (e) => {
    setCustomer({ ...customer, [e.target.name]: e.target.value });
  };

  const checkLoyalty = () => {
    return loyalty.some(
      (entry) => entry.name === customer.name && entry.contact === customer.contact
    );
  };

  const getBillDetails = () => {
    let total = cart.reduce((sum, item) => sum + item.totalPrice, 0);
    let discount = cart.reduce((sum, item) => sum + item.discount, 0);
    let netPay = total - discount;

    if (checkLoyalty()) {
      const extraDiscount = 0.05 * netPay;
      netPay -= extraDiscount;
      alert('Loyalty bonus: Additional 5% discount applied!');
    }

    return { total, discount, netPay };
  };

  const { total, discount, netPay } = getBillDetails();

  const handlePrint = async () => {
    if (!checkLoyalty()) {
      await axios.post('http://localhost:5000/api/loyalty', customer);
    }
    window.print();
  };

  const filteredProducts = products.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ display: 'flex', gap: '20px' }}>
      {/* Left Section - Product List */}
      <div style={{ flex: 1 }}>
        <h3>Product List</h3>
        <input placeholder="Search" value={search} onChange={(e) => setSearch(e.target.value)} />
        <ul>
          {filteredProducts.map((p) => (
            <li key={p._id}>
              {p.name} ({p.quantity}) - Rs.{p.price}
              <button onClick={() => handleAddToCart(p)}>Add</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Middle Section - Customer + Cart */}
      <div style={{ flex: 1 }}>
        <h3>Customer Info</h3>
        <input name="name" placeholder="Name" value={customer.name} onChange={handleCustomerChange} />
        <input name="contact" placeholder="Contact" value={customer.contact} onChange={handleCustomerChange} />
        <h4>Cart Items</h4>
        <ul>
          {cart.map((item, idx) => (
            <li key={idx}>{item.name} x {item.quantity} = Rs.{item.totalPrice - item.discount}</li>
          ))}
        </ul>
      </div>

      {/* Right Section - Bill */}
      <div style={{ flex: 1 }}>
        <h3>Bill</h3>
        <p>Zenventory | Phone: 937304**** | Fairborn-34567</p>
        <p>Customer: {customer.name} | Contact: {customer.contact}</p>
        <hr />
        {cart.map((item, idx) => (
          <p key={idx}>{item.name} - Qty: {item.quantity} - Rs.{item.totalPrice}</p>
        ))}
        <hr />
        <p>Total: Rs.{total}</p>
        <p>Discount: Rs.{discount}</p>
        <p>Net Pay: Rs.{netPay}</p>
        <button onClick={handlePrint}>Print Bill</button>
      </div>
    </div>
  );
}

export default Billing;
