import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Product() {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({ name: '', price: '', quantity: '' });
  const [selectedId, setSelectedId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (error) {
      alert('Failed to fetch products');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAdd = async () => {
    if (!formData.name || !formData.price || !formData.quantity) {
      alert('Please fill all fields to add product');
      return;
    }
    try {
      setLoading(true);
      await axios.post('http://localhost:5000/api/products', formData);
      alert('Product added successfully');
      setFormData({ name: '', price: '', quantity: '' });
      fetchProducts();
    } catch (error) {
      alert('Failed to add product');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdate = async () => {
    if (!selectedId) {
      alert('Select a product to update');
      return;
    }
    if (!formData.name || !formData.price || !formData.quantity) {
      alert('Please fill all fields to update product');
      return;
    }
    try {
      setLoading(true);
      await axios.put(`http://localhost:5000/api/products/${selectedId}`, formData);
      alert('Product updated successfully');
      setFormData({ name: '', price: '', quantity: '' });
      setSelectedId(null);
      fetchProducts();
    } catch (error) {
      alert('Failed to update product');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      setLoading(true);
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      alert('Product deleted successfully');
      if (selectedId === id) {
        setFormData({ name: '', price: '', quantity: '' });
        setSelectedId(null);
      }
      fetchProducts();
    } catch (error) {
      alert('Failed to delete product');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (product) => {
    setFormData({
      name: product.name,
      price: product.price,
      quantity: product.quantity,
    });
    setSelectedId(product._id);
  };

  const handleClear = () => {
    setFormData({ name: '', price: '', quantity: '' });
    setSelectedId(null);
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h2>Product Management</h2>
      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Product Name"
          style={{ marginRight: '0.5rem' }}
        />
        <input
          type="number"
          name="price"
          value={formData.price}
          onChange={handleChange}
          placeholder="Price"
          min="0"
          style={{ marginRight: '0.5rem' }}
        />
        <input
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleChange}
          placeholder="Quantity"
          min="0"
        />
      </div>
      <div style={{ marginBottom: '1rem' }}>
        <button onClick={handleAdd} disabled={loading}>Add</button>
        <button onClick={handleUpdate} disabled={!selectedId || loading} style={{ marginLeft: '0.5rem' }}>Update</button>
        <button onClick={handleClear} disabled={loading} style={{ marginLeft: '0.5rem' }}>Clear</button>
      </div>

      {loading ? (
        <p>Loading products...</p>
      ) : (
        <table border="1" width="100%" cellPadding="5" style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#eee' }}>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.length === 0 ? (
              <tr><td colSpan="4" style={{ textAlign: 'center' }}>No products found</td></tr>
            ) : (
              products.map(product => (
                <tr key={product._id}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.quantity}</td>
                  <td>
                    <button onClick={() => handleSelect(product)}>Select</button>
                    <button onClick={() => handleDelete(product._id)} style={{ marginLeft: '0.5rem' }}>Delete</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Product;
