// src/components/billing.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Billing() {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState({ name: '', contact: '' });
  const [billText, setBillText] = useState('');

  useEffect(() => {
    // Fetch products on mount
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  // Add product to cart with quantity and discount calculation
  const handleAddToCart = (item) => {
    const qtyStr = prompt(`Enter quantity for ${item.name}`);
    const qty = parseInt(qtyStr, 10);
    if (isNaN(qty) || qty <= 0) return alert('Invalid quantity');

    let discount = 0;
    if (qty >= 5) discount = 0.10;
    else if (qty >= 3) discount = 0.05;

    let total = qty * item.price;
    const discountedTotal = total - total * discount;

    alert(`Discount Applied: ${(discount * 100).toFixed(0)}%`);

    // Add or update in cart if product already there
    const existingIndex = cart.findIndex(c => c._id === item._id);
    if (existingIndex !== -1) {
      const newCart = [...cart];
      newCart[existingIndex].qty += qty;
      newCart[existingIndex].total += discountedTotal;
      newCart[existingIndex].discount = discount;
      setCart(newCart);
    } else {
      setCart([...cart, { ...item, qty, total: discountedTotal, discount }]);
    }
  };

  // Generate bill text, save bill to backend, download txt file
  const handleGenerateBill = async () => {
    if (!customer.name || !customer.contact) {
      alert('Please enter customer name and contact');
      return;
    }
    if (cart.length === 0) {
      alert('Cart is empty');
      return;
    }

    let total = 0;
    let discountAmt = 0;

    let lines = `\t\tZenventory\nPhone No. 937304****, Fairborn-34567\n${'='.repeat(46)}\nCustomer Name: ${customer.name}\tPhone No.: ${customer.contact}\nBill No.: ${Date.now()}\t\tDate: ${(new Date()).toLocaleDateString()}\n${'='.repeat(46)}\nProduct Name\t\tQTY\tPrice\n`;

    cart.forEach(item => {
      lines += `${item.name}\t\t${item.qty}\t${item.total.toFixed(2)}\n`;
      total += item.total;
      discountAmt += item.total * item.discount;
    });

    // Check loyalty from backend
    let loyaltyDiscount = 0;
    try {
      const loyaltyRes = await axios.post('http://localhost:5000/api/loyalty/check', customer);
      if (loyaltyRes.data.exists) {
        loyaltyDiscount = 0.05;
        alert('Returning customer - additional 5% discount applied!');
      } else {
        await axios.post('http://localhost:5000/api/loyalty/register', customer);
      }
    } catch (err) {
      console.error('Loyalty API error:', err);
    }

    const loyaltyAmount = total * loyaltyDiscount;
    const netPay = total - discountAmt - loyaltyAmount;

    lines += `${'='.repeat(46)}\nBill Amount\t\t\tRs.${total.toFixed(2)}\n`;
    lines += `Discount\tRs.${discountAmt.toFixed(2)}\n`;
    lines += `Loyalty Discount\tRs.${loyaltyAmount.toFixed(2)}\n`;
    lines += `Net Pay\t\t\tRs.${netPay.toFixed(2)}\n`;
    lines += `${'='.repeat(46)}\nThank You for your Business!`;

    setBillText(lines);

    // Save bill on backend
    try {
      await axios.post('http://localhost:5000/api/generate-bill', {
        customerName: customer.name,
        contact: customer.contact,
        items: cart.map(({ name, qty, price }) => ({ name, quantity: qty, price })),
        total,
        discount: discountAmt + loyaltyAmount,
        netPay,
      });
      alert('Bill saved successfully on server');
    } catch (error) {
      console.error('Failed to save bill:', error);
      alert('Failed to save bill on server');
    }

    // Download bill as text file
    const element = document.createElement('a');
    const file = new Blob([lines], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `bill_${Date.now()}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div style={{ display: 'flex', gap: '20px' }}>
      {/* Left: Products */}
      <div style={{ flex: 1 }}>
        <h3>Products</h3>
        <input
          type="text"
          placeholder="Search product"
          value={search}
          onChange={e => {
            setSearch(e.target.value);
            setFiltered(products.filter(p =>
              p.name.toLowerCase().includes(e.target.value.toLowerCase())
            ));
          }}
        />
        <ul>
          {(search ? filtered : products).map(p => (
            <li key={p._id}>
              {p.name} ({p.quantity}) <button onClick={() => handleAddToCart(p)}>Add</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Middle: Customer + Cart */}
      <div style={{ flex: 1 }}>
        <h3>Customer Details</h3>
        <input
          type="text"
          placeholder="Customer Name"
          value={customer.name}
          onChange={e => setCustomer({ ...customer, name: e.target.value })}
          style={{ display: 'block', marginBottom: '8px' }}
        />
        <input
          type="text"
          placeholder="Contact Number"
          value={customer.contact}
          onChange={e => setCustomer({ ...customer, contact: e.target.value })}
          style={{ display: 'block', marginBottom: '8px' }}
        />

        <h4>Cart</h4>
        <ul>
          {cart.map((item, i) => (
            <li key={i}>{item.name} - {item.qty} pcs - Rs.{item.total.toFixed(2)}</li>
          ))}
        </ul>
      </div>

      {/* Right: Bill */}
      <div style={{ flex: 1 }}>
        <h3>Bill</h3>
        <button onClick={handleGenerateBill}>Generate Bill</button>
        <pre style={{ whiteSpace: 'pre-wrap', marginTop: '20px' }}>{billText}</pre>
      </div>
    </div>
  );
}

export default Billing;
