import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import ZenBot from './ZenBot';
import './LandingPage.css';

function LandingPage() {
  const [showChatbot, setShowChatbot] = useState(false);
  const navigate = useNavigate();

  const toggleChatbot = () => {
    setShowChatbot(prev => !prev);
  };

  const handleVoiceCommand = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    const synth = window.speechSynthesis;

    const speak = (msg) => {
      const utterance = new SpeechSynthesisUtterance(msg);
      synth.speak(utterance);
    };

    speak("Welcome to Zenventory. What section would you like to open? Product or Bill?");
    recognition.start();

    recognition.onresult = (event) => {
      const command = event.results[0][0].transcript.toLowerCase();
      if (command.includes("product")) {
        speak("Opening product section");
        navigate("/product");
      } else if (command.includes("bill")) {
        speak("Opening billing section");
        navigate("/billing");
      } else {
        speak("Sorry, I didn't understand. Please say product or bill.");
      }
    };
  };

  return (
    <div className="landing-container">
      <h1>Zenventory</h1>
      <div className="button-group">
        <Link to="/product"><button>Products</button></Link>
        <Link to="/billing"><button>Billing</button></Link>
      </div>

      <button className="chatbot-icon-btn" onClick={toggleChatbot}>ðŸ’¬</button>
      <button className="chatbot-icon-btn" style={{ right: '100px' }} onClick={handleVoiceCommand}>ðŸŽ¤</button>

      {showChatbot && (
        <div className="chatbot-wrapper">
          <ZenBot />
        </div>
      )}
    </div>
  );
}
