// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Product from './Product';
import Billing from './Billing';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50 flex flex-col">
        <header className="bg-blue-600 text-white text-center py-6 shadow-md">
          <h1 className="text-4xl font-bold">Zenventory</h1>
          <p className="text-sm">Smart Inventory & Billing System</p>
        </header>

        <main className="flex-grow flex items-center justify-center">
          <div className="space-y-8 text-center">
            <h2 className="text-2xl font-semibold">Welcome to Zenventory</h2>
            <div className="flex justify-center space-x-4">
              <Link to="/products" className="bg-green-500 text-white px-6 py-2 rounded hover:bg-green-600">
                Go to Products
              </Link>
              <Link to="/billing" className="bg-purple-500 text-white px-6 py-2 rounded hover:bg-purple-600">
                Go to Billing
              </Link>
            </div>
          </div>
        </main>

        <Routes>
          <Route path="/products" element={<Product />} />
          <Route path="/billing" element={<Billing />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
