import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const Billing = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]); // {productId, name, price, quantity}
  const [customer, setCustomer] = useState({ name: '', contact: '' });
  const [discount, setDiscount] = useState(0);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  // Fetch products on mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(() => setProducts([]));
  }, []);

  // Speech synthesis helper
  const speak = (text) => {
    const utter = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utter);
  };

  // Initialize SpeechRecognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Speech Recognition API not supported in this browser');
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = 'en-US';

    recognitionRef.current.onresult = (event) => {
      const transcript = event.results[0][0].transcript.toLowerCase();
      console.log('Heard:', transcript);
      handleVoiceCommand(transcript);
    };

    recognitionRef.current.onend = () => {
      setListening(false);
    };
  }, []);

  // Start listening for billing voice control
  const startListening = () => {
    if (recognitionRef.current && !listening) {
      speak('Welcome to billing section. You can say add product, apply discount, enter customer details, or generate bill.');
      recognitionRef.current.start();
      setListening(true);
    }
  };

  // Voice command handler for billing
  const handleVoiceCommand = async (command) => {
    if (command.includes('add product')) {
      speak('Please say the product name you want to add.');
      recognitionRef.current.onresult = (event) => {
        const prodName = event.results[0][0].transcript.trim().toLowerCase();
        const product = products.find(p => p.name.toLowerCase() === prodName);
        if (!product) {
          speak(`Product ${prodName} not found.`);
          return;
        }
        speak(`How many units of ${product.name} do you want to add?`);
        recognitionRef.current.onresult = (event) => {
          const qtyText = event.results[0][0].transcript.trim();
          const qty = parseInt(qtyText.replace(/[^0-9]/g, ''), 10);
          if (isNaN(qty) || qty <= 0) {
            speak('Invalid quantity. Please try adding product again.');
            return;
          }
          setCart(prev => {
            const exist = prev.find(item => item.productId === product._id);
            if (exist) {
              // update quantity
              return prev.map(item =>
                item.productId === product._id ? { ...item, quantity: item.quantity + qty } : item
              );
            }
            return [...prev, { productId: product._id, name: product.name, price: product.price, quantity: qty }];
          });
          speak(`${qty} units of ${product.name} added to cart.`);
        };
        recognitionRef.current.start();
      };
      recognitionRef.current.start();
    }
    else if (command.includes('apply discount')) {
      speak('Please say the discount percentage.');
      recognitionRef.current.onresult = (event) => {
        const discText = event.results[0][0].transcript.trim();
        const discValue = parseFloat(discText.replace(/[^0-9.]/g, ''));
        if (isNaN(discValue) || discValue < 0 || discValue > 100) {
          speak('Invalid discount value. Please try again.');
          return;
        }
        setDiscount(discValue);
        speak(`Discount of ${discValue} percent applied.`);
      };
      recognitionRef.current.start();
    }
    else if (command.includes('customer details')) {
      speak('Please say the customer name.');
      recognitionRef.current.onresult = (event) => {
        const custName = event.results[0][0].transcript.trim();
        setCustomer(prev => ({ ...prev, name: custName }));
        speak(`Got customer name: ${custName}. Please say the contact number.`);
        recognitionRef.current.onresult = (event) => {
          const contact = event.results[0][0].transcript.trim();
          setCustomer(prev => ({ ...prev, contact }));
          speak(`Contact number recorded as ${contact}.`);
        };
        recognitionRef.current.start();
      };
      recognitionRef.current.start();
    }
    else if (command.includes('generate bill') || command.includes('checkout')) {
      if (cart.length === 0) {
        speak('Your cart is empty. Please add products before generating the bill.');
        return;
      }
      if (!customer.name || !customer.contact) {
        speak('Customer details are missing. Please provide customer name and contact.');
        return;
      }
      speak('Generating your bill now.');
      // Simulate bill generation
      // You may call your API here to save bill and update product quantities
      setTimeout(() => {
        speak(`Bill generated for ${customer.name}. Total amount is Rs. ${calculateTotal().toFixed(2)} after ${discount}% discount. Thank you for shopping with us.`);
        setCart([]);
        setCustomer({ name: '', contact: '' });
        setDiscount(0);
      }, 1500);
    }
    else {
      speak('Sorry, I did not understand that command. You can say add product, apply discount, enter customer details, or generate bill.');
    }
  };

  // Calculate total price
  const calculateTotal = () => {
    let total = 0;
    cart.forEach(item => {
      total += item.price * item.quantity;
    });
    if (discount > 0) {
      total = total * (1 - discount / 100);
    }
    return total;
  };

  return (
    <div>
      <h2>Billing Section</h2>

      <button
        style={{ marginBottom: '15px', fontSize: '20px', padding: '10px', cursor: 'pointer' }}
        onClick={startListening}
        disabled={listening}
        title={listening ? 'Listening...' : 'Click to start voice billing control'}
      >
        ðŸŽ¤ {listening ? 'Listening...' : 'Start Voice Billing'}
      </button>

      <div>
        <h3>Cart</h3>
        {cart.length === 0 && <p>No products added yet.</p>}
        <ul>
          {cart.map((item, i) => (
            <li key={i}>{item.name} - {item.quantity} units - Rs. {item.price * item.quantity}</li>
          ))}
        </ul>
        <p>Discount: {discount}%</p>
        <p><strong>Total: Rs. {calculateTotal().toFixed(2)}</strong></p>
      </div>

      <div>
        <h3>Customer Details</h3>
        <p>Name: {customer.name || '-'}</p>
        <p>Contact: {customer.contact || '-'}</p>
      </div>
    </div>
  );
};

export default Billing;
