import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [newProd, setNewProd] = useState({ name: '', quantity: '', price: '' });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleAddProduct = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/products', newProd);
      alert('Product added!');
      setNewProd({ name: '', quantity: '', price: '' });
      fetchProducts();
    } catch (err) {
      console.error('Add error:', err);
    }
  };

  return (
    <div className="section">
      <h2>Product Section</h2>

      <input
        type="text"
        placeholder="Product Name"
        value={newProd.name}
        onChange={(e) => setNewProd({ ...newProd, name: e.target.value })}
      />
      <input
        type="number"
        placeholder="Quantity"
        value={newProd.quantity}
        onChange={(e) => setNewProd({ ...newProd, quantity: e.target.value })}
      />
      <input
        type="number"
        placeholder="Price"
        value={newProd.price}
        onChange={(e) => setNewProd({ ...newProd, price: e.target.value })}
      />
      <button onClick={handleAddProduct}>Add Product</button>

      <ul>
        {products.map((p, i) => (
          <li key={i}>
            {p.name} — Qty: {p.quantity}, ₹{p.price}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Product;
