import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import ZenBot from './ZenBot';  // your chatbot component
import './LandingPage.css';

function LandingPage() {
  const [showChatbot, setShowChatbot] = useState(false);
  const [listening, setListening] = useState(false);
  const navigate = useNavigate();

  const toggleChatbot = () => {
    setShowChatbot(prev => !prev);
  };

  // Voice Recognition Setup
  const startListening = () => {
    if (!('webkitSpeechRecognition' in window || 'SpeechRecognition' in window)) {
      alert('Speech Recognition API not supported in this browser.');
      return;
    }

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.start();
    setListening(true);

    recognition.onresult = (event) => {
      const speech = event.results[0][0].transcript.toLowerCase();
      setListening(false);
      handleVoiceCommand(speech);
    };

    recognition.onerror = (event) => {
      setListening(false);
      alert('Error occurred in recognition: ' + event.error);
    };

    recognition.onend = () => {
      setListening(false);
    };
  };

  // Handle recognized voice commands
  const handleVoiceCommand = (speech) => {
    if (speech.includes('product')) {
      alert('Opening Product Section');
      navigate('/product');
    } else if (speech.includes('bill')) {
      alert('Opening Billing Section');
      navigate('/billing');
    } else if (speech.includes('chatbot')) {
      setShowChatbot(true);
    } else {
      alert(`Sorry, I didn't understand: "${speech}"`);
    }
  };

  return (
    <div className="landing-container" data-theme="light">
      <h1 className="welcome-message">Welcome to Zenventory</h1>

      <div className="button-group">
        <Link to="/product"><button>Products</button></Link>
        <Link to="/billing"><button>Billing</button></Link>
      </div>

      {/* Chatbot Icon Button with Tooltip */}
      <div className="btn-tooltip">
        <button
          className={`chatbot-icon-btn ${showChatbot ? 'active' : ''}`}
          onClick={toggleChatbot}
          aria-label="Toggle Chatbot"
          title="Chat with ZenBot"
        >
          ðŸ’¬
        </button>
        <span className="tooltip">Chat with ZenBot</span>
      </div>

      {/* Mic Icon Button with Tooltip */}
      <div className="btn-tooltip">
        <button
          className={`mic-icon-btn ${listening ? 'listening' : ''}`}
          onClick={startListening}
          aria-label="Activate Voice Control"
          title="Voice Control"
        >
          ðŸŽ¤
        </button>
        <span className="tooltip">Voice Control</span>
      </div>

      {/* Chatbot Component */}
      {showChatbot && (
        <div className="chatbot-wrapper fade-in">
          <ZenBot />
        </div>
      )}
    </div>
  );
}

export default LandingPage;
