import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './Product.css';

const Product = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({ name: '', price: '', quantity: '' });
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);

  // Fetch products
  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (e) {
      console.error(e);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Speech synthesis helper
  const speak = (text) => {
    const utter = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utter);
  };

  // Initialize Speech Recognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert('Speech Recognition API not supported in this browser');
      return;
    }
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = false;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = 'en-US';

    recognitionRef.current.onresult = (event) => {
      const transcript = event.results[0][0].transcript.toLowerCase();
      console.log('Heard:', transcript);
      handleVoiceCommand(transcript);
    };

    recognitionRef.current.onend = () => {
      setListening(false);
    };
  }, []);

  // Start listening
  const startListening = () => {
    if (recognitionRef.current && !listening) {
      speak('Welcome to product section. You can say add, update, delete or search a product.');
      recognitionRef.current.start();
      setListening(true);
    }
  };

  // Voice command handler (simplified)
  const handleVoiceCommand = async (command) => {
    if (command.includes('add')) {
      speak('Adding a new product. Please say the product name.');
      recognitionRef.current.onresult = (event) => {
        const name = event.results[0][0].transcript.trim();
        setFormData((prev) => ({ ...prev, name }));
        speak(`Got product name ${name}. Now say the price.`);
        recognitionRef.current.onresult = (event) => {
          const priceText = event.results[0][0].transcript.trim();
          const price = parseFloat(priceText.replace(/[^0-9.]/g, ''));
          setFormData((prev) => ({ ...prev, price: isNaN(price) ? 0 : price }));
          speak(`Price set to ${price}. Finally, say the quantity.`);
          recognitionRef.current.onresult = async (event) => {
            const quantityText = event.results[0][0].transcript.trim();
            const quantity = parseInt(quantityText.replace(/[^0-9]/g, ''), 10);
            setFormData((prev) => ({ ...prev, quantity: isNaN(quantity) ? 0 : quantity }));
            speak(`Quantity set to ${quantity}. Saving product.`);
            // Save product API call
            try {
              await axios.post('http://localhost:5000/api/products', { name, price, quantity });
              speak('Product added successfully.');
              fetchProducts();
              setFormData({ name: '', price: '', quantity: '' });
            } catch (e) {
              speak('Failed to add product.');
            }
          };
          recognitionRef.current.start();
        };
        recognitionRef.current.start();
      };
      recognitionRef.current.start();
    }
    else if (command.includes('delete')) {
      speak('Please say the name of the product to delete.');
      recognitionRef.current.onresult = async (event) => {
        const productName = event.results[0][0].transcript.trim().toLowerCase();
        const product = products.find(p => p.name.toLowerCase() === productName);
        if (!product) {
          speak(`Could not find product named ${productName}.`);
          return;
        }
        speak(`Are you sure you want to delete ${product.name}? Say yes to confirm.`);
        recognitionRef.current.onresult = async (event) => {
          const confirmation = event.results[0][0].transcript.trim().toLowerCase();
          if (confirmation === 'yes') {
            try {
              await axios.delete(`http://localhost:5000/api/products/${product._id}`);
              speak(`${product.name} deleted successfully.`);
              fetchProducts();
            } catch (e) {
              speak('Failed to delete product.');
            }
          } else {
            speak('Deletion cancelled.');
          }
        };
        recognitionRef.current.start();
      };
      recognitionRef.current.start();
    }
    else if (command.includes('update')) {
      speak('Please say the product name you want to update.');
      recognitionRef.current.onresult = async (event) => {
        const productName = event.results[0][0].transcript.trim().toLowerCase();
        const product = products.find(p => p.name.toLowerCase() === productName);
        if (!product) {
          speak(`Could not find product named ${productName}.`);
          return;
        }
        setEditingProduct(product._id);
        setFormData({ name: product.name, price: product.price, quantity: product.quantity });
        speak('What do you want to update? Name, price or quantity?');
        recognitionRef.current.onresult = async (event) => {
          const field = event.results[0][0].transcript.trim().toLowerCase();
          if (!['name', 'price', 'quantity'].includes(field)) {
            speak('Invalid field. Update cancelled.');
            return;
          }
          speak(`Please say the new ${field}`);
          recognitionRef.current.onresult = async (event) => {
            const newValue = event.results[0][0].transcript.trim();
            const updatedData = { ...formData };
            if (field === 'price') {
              const price = parseFloat(newValue.replace(/[^0-9.]/g, ''));
              updatedData.price = isNaN(price) ? formData.price : price;
            } else if (field === 'quantity') {
              const quantity = parseInt(newValue.replace(/[^0-9]/g, ''), 10);
              updatedData.quantity = isNaN(quantity) ? formData.quantity : quantity;
            } else {
              updatedData.name = newValue;
            }
            setFormData(updatedData);
            speak(`Updating product ${field} to ${newValue}.`);
            try {
              await axios.put(`http://localhost:5000/api/products/${product._id}`, updatedData);
              speak('Product updated successfully.');
              fetchProducts();
              setEditingProduct(null);
              setFormData({ name: '', price: '', quantity: '' });
            } catch (e) {
              speak('Failed to update product.');
            }
          };
          recognitionRef.current.start();
        };
        recognitionRef.current.start();
      };
      recognitionRef.current.start();
    }
    else {
      speak('Sorry, I did not understand the command. Please say add, update, or delete.');
    }
  };

  // Form input handler
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Manual form submit handler (same as your original)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingProduct) {
        await axios.put(`http://localhost:5000/api/products/${editingProduct}`, formData);
        alert('Product updated successfully');
      } else {
        await axios.post('http://localhost:5000/api/products', formData);
        alert('Product added successfully');
      }
      setFormData({ name: '', price: '', quantity: '' });
      setEditingProduct(null);
      fetchProducts();
    } catch (error) {
      alert('Failed to save product');
      console.error(error);
    }
  };

  // Edit button handler (manual)
  const handleEdit = (product) => {
    setEditingProduct(product._id);
    setFormData({
      name: product.name,
      price: product.price,
      quantity: product.quantity,
    });
  };

  // Delete button handler (manual)
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      alert('Product deleted successfully');
      fetchProducts();
    } catch (error) {
      alert('Failed to delete product');
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Product Management</h2>

      <button
        style={{ marginBottom: '15px', fontSize: '20px', padding: '10px', cursor: 'pointer' }}
        onClick={startListening}
        disabled={listening}
        title={listening ? 'Listening...' : 'Click to start voice control'}
      >
        ðŸŽ¤ {listening ? 'Listening...' : 'Start Voice Control'}
      </button>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleChange}
          required
        />
        <button type="submit">{editingProduct ? 'Update' : 'Add'} Product</button>
      </form>

      <h3>Product List</h3>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Name</th>
            <th>Price ($)</th>
            <th>Quantity</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>
                  <button onClick={() => handleEdit(product)}>Edit</button>
                  <button onClick={() => handleDelete(product._id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No products found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Product;
