// billing.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Billing = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [billItems, setBillItems] = useState([]);
  const [customer, setCustomer] = useState({ name: '', contact: '' });
  const [searchTerm, setSearchTerm] = useState('');

  const [billSummary, setBillSummary] = useState({ total: 0, discount: 0, netPay: 0 });

  useEffect(() => {
    axios.get('http://localhost:5000/api/products').then((res) => {
      setProducts(res.data);
      setFilteredProducts(res.data);
    });
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    setFilteredProducts(products.filter(p => p.name.toLowerCase().includes(e.target.value.toLowerCase())));
  };

  const handleAddProduct = (product) => {
    const quantity = prompt(`Enter quantity for ${product.name}`);
    if (quantity && !isNaN(quantity)) {
      const total = parseFloat(quantity) * parseFloat(product.price);
      const newItem = { ...product, quantity: parseInt(quantity), total };
      const newBill = [...billItems, newItem];
      setBillItems(newBill);
      const totalAmt = newBill.reduce((sum, item) => sum + item.total, 0);
      const discount = totalAmt * 0.1;
      const netPay = totalAmt - discount;
      setBillSummary({ total: totalAmt, discount, netPay });
    }
  };

  return (
    <div style={{ display: 'flex', gap: '20px' }}>
      {/* Left Section */}
      <div style={{ flex: 1 }}>
        <h3>Product List</h3>
        <input placeholder="Search Product" value={searchTerm} onChange={handleSearch} />
        <ul>
          {filteredProducts.map((p) => (
            <li key={p._id}>
              {p.name} ({p.quantity}) <button onClick={() => handleAddProduct(p)}>Add</button>
            </li>
          ))}
        </ul>
      </div>

      {/* Middle Section */}
      <div style={{ flex: 1 }}>
        <h3>Customer Details</h3>
        <input placeholder="Customer Name" value={customer.name} onChange={(e) => setCustomer({ ...customer, name: e.target.value })} />
        <input placeholder="Contact Number" value={customer.contact} onChange={(e) => setCustomer({ ...customer, contact: e.target.value })} />

        <h3>Selected Items</h3>
        <ul>
          {billItems.map((item, i) => (
            <li key={i}>{item.name} - QTY: {item.quantity} - Total: Rs.{item.total}</li>
          ))}
        </ul>
      </div>

      {/* Right Section */}
      <div style={{ flex: 1 }}>
        <h3>Bill</h3>
        <pre>
          {`\t\tZenventory         \t Phone No. 937304****, Fairborn-34567\n`}
          {`==============================================\n`}
          {`Customer Name: ${customer.name} Phone No.: ${customer.contact}\n`}
          {`Bill No.: 0001\t\tDate: ${new Date().toLocaleDateString()}\n`}
          {`==============================================\n`}
          {`Product Name\t\tQTY\tPrice\n`}
          {`==============================================\n`}
          {billItems.map(item => `${item.name}\t\t${item.quantity}\t${item.total}`).join('\n')}
          {`\n==============================================\n`}
          {`Bill Amount\t\t\t\tRs.${billSummary.total.toFixed(2)}\n`}
          {`Discount (10%)\t\tRs.${billSummary.discount.toFixed(2)}\n`}
          {`Net Pay\t\t\t\tRs.${billSummary.netPay.toFixed(2)}\n`}
          {`==============================================\n`}
          {`\nThank You for your Business!`}
        </pre>
      </div>
    </div>
  );
};

export default Billing;
